mapper.select:
select 

from order_detail
where 1=1

   and order_id = #{orderId}


   and round_id=#{roundId}


   and state = #{state}


   and detail_type in (${detailType})


  and category_id = #{categoryId}


  and detail_id IN

        #{id}


order by create_time DESC

SELECT t2.category_id, (SELECT t1.category_name from product_category t1 where t1.category_id=t2.category_id) as name, t2.value FROM ( SELECT s.category_id, sum(s.product_number) as value from order_detail s,order_master s1 where s1.order_id=s.order_id and s1.state='0' and s1.status='2' and s.state='0'
and FIND_IN_SET(s.category_id, queryCategoryChildren(#{categoryId}))
AND date_format( s1.open_time, '%Y-%m-%d' ) >= #{startDate}
and date_format( s1.open_time, '%Y-%m-%d' ) <= #{endDate}
GROUP BY s.category_id )t2 ORDER BY t2.value DESC

SELECT t2.category_id, (SELECT t1.category_name from product_category t1 where t1.category_id=t2.category_id) as name, t2.value FROM ( SELECT s.category_id, sum(s.product_number*s.product_price) as value from order_detail s,order_master s1 where s1.order_id=s.order_id and s1.state='0' and s1.status='2' and s.state='0' AND s.product_price IS not NULL and s.product_price > 0 and s.product_number is not NULL AND s.product_number > 0
and FIND_IN_SET(s.category_id, queryCategoryChildren(#{categoryId}))
AND date_format( s1.open_time, '%Y-%m-%d' ) >= #{startDate}
and date_format( s1.open_time, '%Y-%m-%d' ) <= #{endDate}
GROUP BY s.category_id )t2 ORDER BY t2.value DESC
